import React, { useState, useEffect } from "react";
import { Input, Button, List, Typography, Space, message } from "antd";
import { PlusOutlined, DeleteOutlined } from "@ant-design/icons";

const { Title } = Typography;

function App() {
  const [task, setTask] = useState("");
  const [todos, setTodos] = useState([]);

  // ‚úî NoCodeAPI URL'ni buraya yazacaksƒ±n
  const apiURL = "https://v1.nocodeapi.com/ulasemili/google_sheets/FSuGYPPofNsUEeLK?tabId=Sayfa1";


  // üì© Yeni g√∂rev ekle
  const addTask = async () => {
    if (!task.trim()) return;

    const newTask = [[task]]; // üëà Dikkat! √áift k√∂≈üeli parantez

    try {
      const response = await fetch(apiURL, {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(newTask) // üëà Sadece newTask, ekstra k√∂≈üeli yok
      });

      if (response.ok) {
        setTodos([...todos, task]);
        setTask("");
        message.success("G√∂rev eklendi!");
      } else {
        message.error("Veri g√∂nderilemedi.");
      }
    } catch (error) {
      message.error("Bir hata olu≈ütu.");
      console.error(error);
    }
  };


  // üßπ G√∂rev sil
  const deleteTask = async (index) => {
    try {
      const updatedTodos = todos.filter((_, i) => i !== index);
      setTodos(updatedTodos);

      // Google Sheet verisini g√ºncellemek istersen burada ek i≈ü gerekebilir
      message.success("G√∂rev silindi (ancak NoCodeAPI ile senkron deƒüil)");
    } catch (error) {
      message.error("Silme i≈ülemi ba≈üarƒ±sƒ±z.");
    }
  };

  // üóÇ Ba≈ülangƒ±√ßta veriyi √ßek (isteƒüe baƒülƒ±)
  const fetchTodos = async () => {
    try {
      const response = await fetch(apiURL);
      const data = await response.json();
      const list = data.data.map((item) => item[0]);
      setTodos(list);
    } catch (error) {
      console.error("Veri alƒ±namadƒ±.");
    }
  };

  useEffect(() => {
    fetchTodos();
  }, []);

  return (
    <div style={{ maxWidth: 500, margin: "40px auto" }}>
      <Title level={2}>üìù To-Do List</Title>
      <Space.Compact style={{ width: "100%" }}>
        <Input
          placeholder="Bir g√∂rev yaz..."
          value={task}
          onChange={(e) => setTask(e.target.value)}
        />
        <Button type="primary" icon={<PlusOutlined />} onClick={addTask}>
          Ekle
        </Button>
      </Space.Compact>

      <List
        style={{ marginTop: 24 }}
        bordered
        dataSource={todos}
        renderItem={(item, index) => (
          <List.Item
            actions={[
              <Button
                danger
                shape="circle"
                icon={<DeleteOutlined />}
                onClick={() => deleteTask(index)}
              />
            ]}
          >
            {item}
          </List.Item>
        )}
      />
    </div>
  );
}

export default App;
